// Book.js
import React from 'react'
import './Book.css'

const Book = ({ id, title, author, price, handleRemove }) => {
  return (
    <div id='book-container'>
      <span id='container-data1' className='book-data'>{id}</span>
      <span id='container-data2' className='book-data'>{title}</span>
      <span id='container-data3' className='book-data'>{author}</span>
      <span id='container-data4' className='book-data'>{price}</span>
      <button className='remove-btn' onClick={() => handleRemove(id)}>REMOVE</button>
    </div>
  )
}

export default Book
