import { useEffect, useState } from "react";
import "./App.css";
import Book from "./components/Book";
import Addbook from "./components/Addbook";

function App() {

  let[enteredId,setEnteredId] = useState("");
  let[enteredTitle,setEnteredTitle] = useState("");
  let[enteredAuthor,setEnteredAuthor] = useState("");
  let[enteredPrice,setEnteredPrice] = useState("");
  
  let [books, setBooks] = useState(null);



  useEffect(()=>{
    fetch('http://localhost:8000/books')
  .then((response)=>{
    return response.json();
  })
  .then((data)=>{
    setBooks(data);
    console.log(data);
  })
  },[])

  function handleRemove(id) {
    let newBooks = books.filter((element) => {
      return element.id !== id;
    });
    setBooks(newBooks);
  }

  function handleSubmit(event){
    event.preventDefault();

    let book ={
      id:enteredId,
      title:enteredTitle,
      author:enteredAuthor,
      price:enteredPrice
    }

    fetch('http://localhost:8000/books', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      },
      body: JSON.stringify(book)
  })
  .then((data)=>{
    let newBooks = [...books];
    newBooks.push(book);

    setBooks(newBooks); 
})
}

  return (
    <div id="main-container">
        <Addbook 
           handleSubmit={handleSubmit}
           setEnteredId={setEnteredId} 
           setEnteredTitle={setEnteredTitle} 
           setEnteredAuthor={setEnteredAuthor}
           setEnteredPrice={setEnteredPrice} 
        />

      {
      books &&
      books.map((element) => {
        return (
          <Book
            key={element.id}
            id={element.id}
            title={element.title}
            author={element.author}
            price={element.price}
            books={books}
            setBooks={setBooks}
            handleRemove={handleRemove}
          />
        );
      })}
    </div>
  );
}

export default App;
