import React, { useState } from 'react';
import './AddBook.css';

function AddBook(props) {
    const [newBook, setNewBook] = useState({ id: '', title: '', author: '', price: '' });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setNewBook(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        props.handleSubmit(newBook);
        setNewBook({ id: '', title: '', author: '', price: '' });
    };

    return (
        <div className="form-container">
            <div className="inner-container">
                <form className='form-div' onSubmit={handleSubmit}>
                    <input type="text" className="input-data" name="id" value={newBook.id} onChange={handleChange} placeholder='Book Id' required />
                    <input type="text" className="input-data" name="title" value={newBook.title} onChange={handleChange} placeholder='Enter book title' required />
                    <input type="text" className="input-data" name="author" value={newBook.author} onChange={handleChange} placeholder='Enter book author' required />
                    <input type="text" className="input-data" name="price" value={newBook.price} onChange={handleChange} placeholder='Enter book price' required />
                    <input type="submit" value="Add" className="btn" />
                </form>
            </div>
        </div>
    );
}

export default AddBook;
