import React, { useState } from 'react'
import Book from './components/Book'
import './App.css'

const App = () => {
  const [books, setBooks] = useState([
    {
      id: 1,
      title: "ikigai",
      author: "hector",
      price: 150
    },
    {
      id: 2,
      title: "Harry Potter",
      author: "J.K Rolling",
      price: 400
    },
    {
      id: 3,
      title: "RICH DAD POOR DAD",
      author: "Robert Kiyoski",
      price: 200
    },
    {
      id: 4,
      title: "Your Name",
      author: "Mokoto",
      price: 300
    },
  ]);

  const [newBook, setNewBook] = useState({
    title: "",
    author: "",
    price: 0
  });

  const handleAddBook = () => {
    const id = books.length + 1;
    setBooks([...books, { id, ...newBook }]);
    setNewBook({ title: "", author: "", price: 0 });
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewBook({ ...newBook, [name]: value });
  };

  const handleRemove = (idToRemove) => {
    setBooks(prevBooks => prevBooks.filter(book => book.id !== idToRemove));
  };

  return (
    <div id='main-container'>
      <div id="add-book-form">
        <input type="text" name="title" placeholder="Title" value={newBook.title} onChange={handleInputChange} />
        <input type="text" name="author" placeholder="Author" value={newBook.author} onChange={handleInputChange} />
        <input type="number" name="price" placeholder="Price" value={newBook.price} onChange={handleInputChange} />
        <button onClick={handleAddBook}>Add Book</button>
      </div>
      {books.map((element) => (
        <Book
          key={element.id}
          id={element.id}
          title={element.title}
          author={element.author}
          price={element.price}
          handleRemove={handleRemove}
        />
      ))}
    </div>
  );
};

export default App;


















