import React, { useState } from 'react';
import './AddBook.css';

function AddBook(props) {
  const [id, setId] = useState('');
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [price, setPrice] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    props.handleSubmit({ id, title, author, price: parseInt(price) });
    setId('');
    setTitle('');
    setAuthor('');
    setPrice('');
  };

  return (
    <div className='form-container'>
      <form className='form-div' onSubmit={handleSubmit}>
        id: <input type='text' id='id' value={id} onChange={(e) => setId(e.target.value)} />
        Title: <input type='text' id='title' value={title} onChange={(e) => setTitle(e.target.value)} />
        Author: <input type='text' id='author' value={author} onChange={(e) => setAuthor(e.target.value)} />
        Price: <input type='text' id='price' value={price} onChange={(e) => setPrice(e.target.value)} />
        <input type='submit' value='add' id='add-btn' />
      </form>
    </div>
  );
}

export default AddBook;
