  const [books, setBooks] = useState([
    {
      id: 1,
      title: "ikigai",
      author: "hector",
      price: 150
    },
    {
      id: 2,
      title: "Harry Potter",
      author: "J.K Rolling",
      price: 400
    },
    {
      id: 3,
      title: "RICH DAD POOR DAD",
      author: "Robert Kiyoski",
      price: 200
    },
    {
      id: 4,
      title: "Your Name",
      author: "Mokoto",
      price: 300
    },
  ]);

  // const handleRemove = (idToRemove) => 
