// App.js
import React, { useState } from 'react'
import Book from './components/Book'
import './App.css'

const App = () => {
  const [books, setBooks] = useState([
    {
      id: 1,
      title: "ikigai",
      author: "hector",
      price: 150
    },
    {
      id: 2,
      title: "Harry Potter",
      author: "J.K Rolling",
      price: 400
    },
    {
      id: 3,
      title: "RICH DAD POOR DAD",
      author: "Robert Kiyoski",
      price: 200
    },
    {
      id: 4,
      title: "Your Name",
      author: "Mokoto",
      price: 300
    },
  ]);

  const handleRemove = (idToRemove) => {
    setBooks(prevBooks => 
                prevBooks.filter(book => book.id !== idToRemove));
  }

  return (
    <div id='main-container'>
      {books.map((element) => (
        <Book
          key={element.id}
          id={element.id}
          title={element.title}
          author={element.author}
          price={element.price}
          handleRemove={handleRemove}
        />
      ))}
    </div>
  )
}

export default App
