    }


    return(

        <div id="main-container">
        <AddBook handleSubmit={handleSubmit}
                 setId={setId}
                 setTitle={setTitle}
                 setAuthor={setAuthor}
                 setPrice={setPrice}
                 />

            {
                books.map(
                    (element)=>{
                        return <Book 
                                    key={element.id} 
                                    id={element.id} 
                                    title={element.title} 
                                    author={element.author} 
                                    price={element.price} 
                                    handleRemove={handleRemove}>    
                                </Book>
                    }
                )
            }                    
        </div>
    );
}

export default App;
