import React, { useState } from 'react';
import './AddBook.css';

function AddBook(props) {
    const [enteredId, setEnteredId] = useState("");
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAuthor, setEnteredAuthor] = useState("");
    const [enteredPrice, setEnteredPrice] = useState("");

    function handleChangeId(event) {
        setEnteredId(event.target.value);
    }

    function handleChangeTitle(event) {
        setEnteredTitle(event.target.value);
    }

    function handleChangeAuthor(event) {
        setEnteredAuthor(event.target.value);
    }

    function handleChangePrice(event) {
        setEnteredPrice(event.target.value);
    }

    function handleSubmit(event) {
        event.preventDefault();
        props.handleSubmit({
            id: enteredId,
            title: enteredTitle,
            author: enteredAuthor,
            price: enteredPrice
        });
        // Clear input fields after submission
        setEnteredId("");
        setEnteredTitle("");
        setEnteredAuthor("");
        setEnteredPrice("");
    }

    return (
        <div className="form-container">
            <div className="inner-container">
                <form className='form-div' onSubmit={handleSubmit}>
                    <input type="text" className="input-data" value={enteredId} onChange={handleChangeId} placeholder='Book Id' required />
                    <input type="text" className="input-data" value={enteredTitle} onChange={handleChangeTitle} placeholder='Enter book title' required />
                    <input type="text" className="input-data" value={enteredAuthor} onChange={handleChangeAuthor} placeholder='Enter book author' required />
                    <input type="text" className="input-data" value={enteredPrice} onChange={handleChangePrice} placeholder='Enter book price' required />
                    <input type="submit" value="Add" id="add-btn" />
                </form>
            </div>
        </div>  
    );
}

export default AddBook;
