import React, { useState } from 'react';
import Book from './components/Book';
import AddBook from './components/AddBook';
import useFetch from './useFetch';

function App() {
    const [books, setBooks] = useState(null);
    const { error } = useFetch('http://localhost:8000/books', setBooks);

    const handleRemove = (id) => {
        // Function to remove book
    };

    const handleSubmit = (newBook) => {
        // Function to handle submission of new book
    };

    return (
        <div id="main-container">
            <AddBook handleSubmit={handleSubmit} />
            {!error && books &&
                books.map(book => (
                    <Book
                        key={book.id}
                        {...book}
                        handleRemove={handleRemove}
                        books={books}
                        setBooks={setBooks} // Ensure setBooks is passed correctly
                    />
                ))
            }
            {error && <p>Error: {error}</p>}
        </div>
    );
}

export default App;
