import React from 'react';
import './AddBook.css';

function AddBook(props){

    function handleChangeId(event) {
        props.setEnteredId(event.target.value);
    }

    function handleChangeTitle(event) {
        props.setEnteredTitle(event.target.value);
    }

    function handleChangeAuthor(event) {
        props.setEnteredAuthor(event.target.value);
    }

    function handleChangePrice(event) {
        props.setEnteredPrice(event.target.value);
    }


    return (
        <div className="form-container">
            <div className="inner-container">
            <form className='form-div' onSubmit={props.handleSubmit}>
                id: <input type = "text" id="id" onChange={handleChangeId} placeholder='Book_Id' required></input>
                Title: <input type = "text" id="title" onChange={handleChangeTitle} placeholder='Enter book title' required></input>
                Author: <input type = "text" id="author" onChange={handleChangeAuthor} placeholder='Enter book author' required></input>
                Price: <input type = "text" id="price" onChange={handleChangePrice} placeholder='Enter book price' required></input>

                <input type="submit" value="add" id="add-btn"/>
            
            </form>
            </div>

            
        </div>  
    );
}

export default AddBook;
