import {useState} from 'react';
import Book from './components/Book';
import AddBook from './components/AddBook';

function App() {
    let[enteredId, setEnteredId] = useState("");
    let[enteredTitle, setEnteredTitle] = useState("");
    let[enteredAuthor, setEnteredAuthor] = useState("");
    let[enteredPrice, setEnteredPrice] = useState("");

    let[books, setBooks] = useState(null);
    //                     4
    function handleRemove(id){
        let newBooks = books.filter(
            (element)=>{
                return element.id !==id;
            }
        )
        setBooks(newBooks);
    }   

    function handleSubmit(event) {
        event.preventDefault();
        let book = {
                      id: enteredId,
                      title: enteredTitle,
                      author: enteredAuthor,
                      price: enteredPrice
                   }
        let newBooks = [...books];
        newBooks.push(book);

        setBooks(newBooks);
       
    }


    function updateData() {

    }


    return(
        <div id="main-container">
            
            <AddBook  
                handleSubmit={handleSubmit} 
                setEnteredId={setEnteredId} 
                setEnteredTitle={setEnteredTitle} 
                setEnteredAuthor={setEnteredAuthor} 
                setEnteredPrice={setEnteredPrice}>
            </AddBook>
            {
                books.map(
                    (element)=>{
                        return <Book 
                                    key={element.id} 
                                    id={element.id} 
                                    title={element.title} 
                                    author={element.author} 
                                    price={element.price} 
                                    handleRemove={handleRemove}
                                    updateData = {updateData}
                                    books = {books}
                                    setBooks = {setBooks}
                                    >    
                                </Book>
                    }
                )
            }                    
        </div>
    );
}

export default App;
